        this.hasItem(a) ? (d = this.hasItem(a), d.quantity = parseInt(d.quantity, 10) + parseInt(a.quantity, 10), a = d) : this.items[a.id] = a;
        this.update();
        return a
    };
    a.remove = function(c) {
        var b = {};
        a.each(function(a) {
            a.id !== c && (b[a.id] = a)
        });
        this.items = b
    };
    a.empty = function() {
        simpleCart.items = {};
        simpleCart.update()
    };
    a.find = function(c) {
        if (!c) return null;
        var b = [];
        a.each(function(d) {
            fits = !0;
            a.each(c, function(c, a, b) {
                if (!d[b] || d[b] != c) fits = !1
            });
            fits && b.push(d)
        });
        return 0 === b.length ? null : b
    };
    a.each = function(c, b) {
        var d, e = 0,
            g;
        if ("function" === typeof c) var h = c,
            f = a.items;
        else if ("function" === typeof b) var h = b,
            f = c;
        else return;
        for (d in f)
            if ("function" !== typeof f[d]) {
                g = h.call(a, f[d], e, d);
                if (!1 === g) break;
                e++
            }
    };
