$(document).ready(function()
	{
	$("a.poplight[href^=#]").click(function()
		{
		var a=$(this).attr("rel"),b=$(this).attr("href").split("?")[1].split("&")[0].split("=")[1];
		$("#"+a).fadeIn().css(
			{
			width:Number(b)
		}
		).prepend('<a href="#" class="close"><img border="0" src="https://lh4.googleusercontent.com/-zOZMdvZpJe8/UFyZrvuhb_I/AAAAAAAADnY/YAHqXFXGhBI/s23/close.png" class="btn_close" title="\u0110\u00f3ng gi\u1ecf h\u00e0ng" alt="Close" /></a>');
		var b=($("#"+a).height()+80)/2,c=($("#"+a).width()+80)/2;
		$("#"+a).css(
			{
			"margin-top":-b,"margin-left":-c
		}
		);
		$("body").append('<div id="fade"></div>');
		$("#fade").css(
			{
			filter:"alpha(opacity=80)"
		}
		).fadeIn();
		return!1
	}
	);
	$("a.close, #fade").live("click",function()
		{
		$("#fade , .popup_block").fadeOut(function()
			{
			$("#fade, a.close").remove()
		}
		);
		return!1
	}
	)
}
);
